def replace(this, that, string):
    return string.replace(this, that)

def split_string_index(string, index):
    try:
        empty_1 = ''
        for i in range(index):
            empty_1 += string[i]
        empty_2 = ''
        for j in range(index, len(string)):
            empty_2 += string[j]
        return [empty_1, empty_2]
    except IndexError:
        return [string, '']
        
def linebreak(string, numchar_linebreak):
    count = 0 
    index = 0
    str_add = r'\\&'
    for _ in string:
        if count == numchar_linebreak and count != 0:
            if _ == ' ': 
                string_split = split_string_index(string, index)
                string = string_split[0] + str_add + string_split[1][1:]
                index += len(str_add)
                count = 0 
            else: 
                string_split = split_string_index(string, index)
                i = 0 
                for __ in string_split[1]:
                    if __ == ' ':
                        # print(string_split[1][i])
                        string_split_2 = split_string_index(string_split[1], i)
                        # print(string_split)
                        string_split[1] = string_split_2[0] + str_add + string_split_2[1][1:]
                        string = string_split[0] + string_split[1]
                        index += len(str_add)
                        count = 0 
                        break
                    i += 1
        count += 1
        index += 1
    return string

def format_string(string):
    string = linebreak(string, numchar_linebreak)
    string = replace("m1", "m_1", string)
    string = replace("m2", "m_2", string)
    string = replace("m3", "m_3", string)
    string = replace("m4", "m_4", string)
    string = replace("mx", "m_X", string)

    string = replace("sproph^2", r"|D_{\rm BW}^h(s)|^2", string)
    string = replace("tproph^2", r"|D_{\rm BW}^h(t)|^2", string)
    string = replace("uproph^2", r"|D_{\rm BW}^h(u)|^2", string)

    string = replace("spropX^2", r"|D_{\rm BW}^h(s)|^2", string)
    string = replace("tpropX^2", r"|D_{\rm BW}^h(t)|^2", string)
    string = replace("upropX^2", r"|D_{\rm BW}^h(u)|^2", string)

    string = replace("spropX", r"D_{\rm BW}^X(s)", string)
    string = replace("tpropX", r"D_{\rm BW}^X(t)", string)
    string = replace("upropX", r"D_{\rm BW}^X(u)", string)

    string = replace("sproph", r"D_{\rm BW}^h(s)", string)
    string = replace("tproph", r"D_{\rm BW}^h(t)", string)
    string = replace("uproph", r"D_{\rm BW}^h(u)", string)

    string = replace("g^4", r"\tilde{g}^4", string)

    return string

